import React, { useEffect, useRef } from "react";
import { useSelector } from "react-redux";
import {
  addItem,
  setProductForm,
  toggleProductForm,
  updateItem,
} from "../../features/form-slice";
import { useDispatch } from "react-redux";
import "../css/AddEditProduct.css";
import { GrAdd } from "react-icons/gr";
import { ImCancelCircle } from "react-icons/im";
import { useFormik } from "formik";
import * as Yup from "yup";

const validationSchema = Yup.object().shape({
  Name: Yup.string()
    .required("Dont be silly, you need a name!!!")
    .min(5, "Must be at least 5 chars long")
    .max(50, "50 chars is enough!"),
  Description: Yup.string()
    .required("Dont be silly, you need a description!!!")
    .min(5, "Must be at least 5 chars long")
    .max(50, "50 chars is enough!"),
  ImageName: Yup.string()
    .required("Dont be silly, you need an ImageName!!!")
    .min(5, "Must be at least 5 chars long")
    .max(50, "50 chars is enough!"),
  Rating: Yup.number()
    .required("Dont be silly, you need a Rating!!!")
    .min(1, "Rating must be between 1 and 5")
    .max(5, "Rating must be between 1 and 5"),
});

function NewProduct() {
  const productToEdit = useSelector((state) => state.form.productToEdit);
  const showProductForm = useSelector((state) => state.form.showProductForm);
  const dispatch = useDispatch();

  const formik = useFormik({
    initialValues: {
      Name: productToEdit ? productToEdit.name : "",
      Description: productToEdit ? productToEdit.description : "",
      ImageName: productToEdit ? productToEdit.imageName : "",
      Rating: productToEdit ? productToEdit.rating : "",
    },
    validationSchema: validationSchema,
    onSubmit: (values) => {
      const item = {
        id: productToEdit ? productToEdit.id : 0,
        name: values.Name,
        description: values.Description,
        imageName: values.ImageName,
        rating: values.Rating,
      };

      fetch("https://localhost:7201/api/Products", {
        method: productToEdit ? "put" : "post",
        headers: {
          "Content-Type": "application/json",
          Authorization: "Bearer " + localStorage.getItem("token"),
        },
        body: JSON.stringify(item),
      })
        .then((response) => {
          if (!response.ok) {
            throw new Error(response.status);
          }
          if (productToEdit) {
            return response;
          } else {
            return response.json();
          }
        })
        .then((data) => {
          if (productToEdit) {
            dispatch(updateItem(item));
          } else {
            dispatch(addItem(data));
          }
          dispatch(setProductForm(false));
          window.location.reload(false);
        })
        .catch((error) => {
          console.error(error);
        });
    },
  });

  return [
    showProductForm && (
      <div className="addEditTable" key="1">
        <span className="title">{productToEdit ? "Edit" : "New"} Product</span>
        <form onSubmit={formik.handleSubmit} className="addEditForm">
          <div className="formColumn">
            <label htmlFor="Name" className="formLabel">
              Name:
            </label>
            <div className="col-sm-2">
              <input
                type="text"
                name="Name"
                onChange={formik.handleChange}
                onBlur={formik.handleBlur}
                value={formik.values.Name}
                className="form-control"
                id="Name"
              />
              {formik.touched.Name && formik.errors.Name && (
                <div className="error">{formik.errors.Name}</div>
              )}
            </div>
          </div>
          {/* Repeat similar structure for other form fields */}
          {/* ... */}
          <div className="mb-2">
            <button className="btn btn-primary" type="submit">
              Save
            </button>
          </div>
        </form>
      </div>
    ),
    <button
      key="2"
      className="addCancelButton"
      onClick={() => dispatch(toggleProductForm())}
    >
      {showProductForm ? <ImCancelCircle /> : <GrAdd />}
    </button>,
  ];
}

export default NewProduct;
