import React, { useState } from "react";
import { closeSignupForm, openLoginForm } from "../../features/form-slice";
import { useDispatch } from "react-redux";

function Signup() {
  const [userName, setUserName] = useState("");
  const [password, setPassword] = useState("");
  const [email, setEmail] = useState("");
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");

  const dispatch = useDispatch();

  const handleOpenLoginCloseSignup = () => {
    dispatch(closeSignupForm());
    dispatch(openLoginForm());
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const newUser = {
      userName: userName,
      password: password,
      email: email,
      firstName: firstName,
      lastName: lastName,
    };
    try {
      const response = await fetch("https://localhost:7149/api/Users", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(newUser),
      });
      if (response.ok) {
        console.log("User created successfully");
      } else {
        console.error("Error creating user");
      }
    } catch (error) {
      console.error("Error creating user: ", error);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="loginForm">
      <input
        type="text"
        className="textInput"
        placeholder="Username"
        value={userName}
        onChange={(event) => setUserName(event.target.value)}
      />
      <input
        type="password"
        className="textInput"
        placeholder="Password"
        value={password}
        onChange={(event) => setPassword(event.target.value)}
      />{" "}
      <input
        type="text"
        className="textInput"
        placeholder="Email"
        value={password}
        onChange={(event) => setEmail(event.target.value)}
      />
      <input
        type="text"
        className="textInput"
        placeholder="First Name"
        value={firstName}
        onChange={(event) => setFirstName(event.target.value)}
      />
      <input
        type="text"
        className="textInput"
        placeholder="Last Name"
        value={lastName}
        onChange={(event) => setLastName(event.target.value)}
      />
      <button className="buttonForm" type="submit">
        Sign Up
      </button>
      <button onClick={handleOpenLoginCloseSignup}>Log in</button>
    </form>
  );
}

export default Signup;
