import { createSlice } from "@reduxjs/toolkit";

export const formSlice = createSlice({
  name: "form",
  initialState: {
    showLoginForm: false,
    showPasswordRecoveryForm: false,
  },
  reducers: {
    toggleLoginForm: (state) => {
      state.showLoginForm = !state.showLoginForm;
      // console.log(state.showLoginForm);
    },
    togglePasswordRecoveryForm: (state) => {
      state.showPasswordRecoveryForm = !state.showPasswordRecoveryForm;
      state.showLoginForm = !state.showLoginForm;
      // console.log(state.showLoginForm);
      // console.log(state.showPasswordRecoveryForm);
    },
  },
});

export default formSlice.reducer;
export const { toggleLoginForm, togglePasswordRecoveryForm } =
  formSlice.actions;
