import React from "react";
import { useState } from "react";
import "./Signup.css";

function Signup({ onSignupClick }) {
  const [usernameOrEmail, setUsernameOrEmail] = useState("");
  const [password, setPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await fetch("/api/users", {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ userNameOrEmail: usernameOrEmail, password }),
      });

      if (!response.ok) {
        const data = await response.json();
        throw new Error(data);
      }

      // The update succeeded, clear the form and error message
      setUsernameOrEmail("");
      setPassword("");
      setErrorMessage("");
    } catch (error) {
      // The update failed, show an error message
      setErrorMessage(error.message);
    }
  };

  return (
    <form className="loginForm">
      <div className="labelDiv">
        <label>Username or email</label>
        <input
          className="textInput"
          required
          type="text"
          id="usernameOrEmail"
          name="usernameOrEmail"
          value={usernameOrEmail}
          onChange={(event) => setUsernameOrEmail(event.target.value)}
        />
      </div>
      <div className="labelDiv">
        <label>Password</label>
        <input
          className="textInput"
          required
          id="password"
          name="password"
          value={password}
          onChange={(event) => setPassword(event.target.value)}
        />
      </div>
      {errorMessage && <div>{errorMessage}</div>}
      <button className="buttonForm" type="submit">
        Submite
      </button>
      <button onClick={onSignupClick}>Log in</button>
    </form>
  );
}

export default Signup;
