import React from "react";
import "./Header.css";
// import Login from "../login/Login";
// import { useState } from "react";
import { openLoginForm } from "../../features/form-slice";
import { useDispatch } from "react-redux";
import jwtDecode from "jwt-decode";

function Header() {
  const dispatch = useDispatch();

  const handleOpenLoginForm = () => {
    dispatch(openLoginForm());
  };

  const token = localStorage.getItem("token"); // Get the token value from localStorage
  const decodedToken = jwtDecode(token);
  const tokenName = decodedToken.given_name;

  localStorage.removeItem("token");

  return (
    <div className="header">
      <div className="header-div">
        {/* <button className="buttonLoginForm" onClick={handleOpenLoginForm}>
          Login
        </button> */}
        {token ? ( // Use a ternary operator to conditionally render the button or the token value
          <button className="buttonLoginForm">Hey {tokenName}</button> // Render the token value
        ) : (
          <button className="buttonLoginForm" onClick={handleOpenLoginForm}>
            Login
          </button> // Render the login button
        )}
        <a className="Logo" href=" ">
          Logo
        </a>
      </div>
      <div className="nav-div">
        <ul className="nav-div-links">
          <a className="a" href=" ">
            צור קשר
          </a>
          <a className="a" href=" ">
            מאמרים
          </a>
          <a className="a" href="/Products">
            מוצרים
          </a>
          <a className="a" href="/About">
            אודות
          </a>
          <a className="a" href="/">
            עמוד הבית
          </a>
        </ul>
      </div>
    </div>
  );
}

export default Header;
