import { createSlice } from "@reduxjs/toolkit";

export const formSlice = createSlice({
  name: "form",
  initialState: {
    showLoginForm: false,
    showPasswordRecoveryForm: false,
  },
  reducers: {
    openLoginForm: (state) => {
      state.showLoginForm = true;
      // console.log(state.showLoginForm);
    },
    closeLoginForm: (state) => {
      state.showLoginForm = true;
      // console.log(state.showLoginForm);
    },
    openPasswordRecoveryForm: (state) => {
      state.showPasswordRecoveryForm = true;
      // console.log(state.showPasswordRecoveryForm);
    },
    closePasswordRecoveryForm: (state) => {
      state.showPasswordRecoveryForm = false;
      // console.log(state.showPasswordRecoveryForm);
    },
  },
});

export default formSlice.reducer;
export const { openLoginForm, closeLoginForm, togglePasswordRecoveryForm } =
  formSlice.actions;
