import React from "react";
import "./Login.css";
import { AiOutlineClose } from "react-icons/ai";
import { useState } from "react";
import jwt_decode from "jwt-decode";
import { useDispatch } from "react-redux";
import {
  toggleLoginForm,
  togglePasswordRecoveryForm,
} from "../../features/form-slice";

function Login({ onCloseLoginClick, onPasswordRecoveryClick }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [token, setToken] = useState("");
  const [error, setError] = useState("");

  const dispatch = useDispatch();

  const handleLoginFormToggle = () => {
    dispatch(toggleLoginForm());
  };
  const handlePasswordRecoveryFormToggle = () => {
    dispatch(togglePasswordRecoveryForm());
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    const url = "https://localhost:7149/api/Authentication";
    const data = {
      userName: username,
      password: password,
    };

    fetch(url, {
      method: "POST",
      body: JSON.stringify(data),
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((response) => response.text())
      .then((data) => {
        if (data === "User not found") {
          setError("User not found");
          // setError(null);
        } else if (data === "UserName or Password wrong") {
          setError("UserName or Password wrong");
          // setError(null);
        } else {
          setToken(data);
          localStorage.setItem("token", data);
          console.log(localStorage.getItem("token"));
          // setError(null);
          // setError(null);
          onCloseLoginClick();
        }
      })
      .catch((error) => console.error(error));
  };

  return (
    <div className="behindTheForm">
      <form className="loginForm" onSubmit={handleSubmit}>
        <button className="closeFormButton" onClick={handleLoginFormToggle}>
          <AiOutlineClose />
        </button>
        <p className="headerForm">Login Form</p>
        <div className="labelDiv">
          <label htmlFor="username">Username</label>
          <input
            className="textInput"
            required
            type="text"
            id="username"
            name="username"
            value={username}
            onChange={(event) => setUsername(event.target.value)}
          />
        </div>
        <div className="labelDiv">
          <label htmlFor="password">Password</label>
          <input
            className="textInput"
            required
            id="password"
            name="password"
            value={password}
            onChange={(event) => setPassword(event.target.value)}
          />
        </div>
        {error && <p className="errorMessage">{error}</p>}
        <button className="buttonForm">Submite</button>
        <button onClick={onPasswordRecoveryClick}>I forgot password</button>
      </form>
    </div>
  );
}

export default Login;
