import React, { useEffect, useRef } from "react";
import { useSelector } from "react-redux";
import {
  addItem,
  setProductForm,
  toggleProductForm,
  updateItem,
} from "../../features/form-slice";
import { useDispatch } from "react-redux";
import "../css/AddEditProduct.css";

function NewProduct() {
  const productToEdit = useSelector((state) => state.form.productToEdit);
  const showProductForm = useSelector((state) => state.form.showProductForm);
  const dispatch = useDispatch();

  const inputName = useRef(null);
  const inputDescription = useRef(null);
  const inputImageName = useRef(null);
  const inputRating = useRef(null);

  useEffect(() => {
    if (productToEdit) {
      inputName.current.value = productToEdit.name;
      inputDescription.current.value = productToEdit.description;
      inputImageName.current.value = productToEdit.imageName;
      inputRating.current.value = productToEdit.rating;
    }
  }, [productToEdit]);

  const handleSave = (e) => {
    e.preventDefault();

    var item = {
      id: productToEdit ? productToEdit.id : 0,
      name: inputName.current.value,
      description: inputDescription.current.value,
      imageName: inputImageName.current.value,
      rating: inputRating.current.value,
    };

    fetch("https://localhost:7201/api/Products", {
      method: productToEdit ? "put" : "post",
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + localStorage.getItem("token"),
      },
      body: JSON.stringify(item),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error(response.status);
        }
        if (productToEdit) {
          return response;
        } else {
          return response.json();
        }
      })
      .then((data) => {
        window.location.reload(false);
        if (productToEdit) {
          dispatch(updateItem(item));
        } else {
          dispatch(addItem(data));
        }
        dispatch(setProductForm(false));
      })
      .catch((error) => {
        console.error(error);
      });
  };

  return [
    showProductForm && (
      <div className="addEditTable" key="1">
        <span>{productToEdit ? "Edit" : "New"} Product</span>
        <form onSubmit={handleSave} className="addEditForm">
          <div className="formColumn">
            <label htmlFor="Name" className="formLabel">
              Name:
            </label>
            <div className="col-sm-2">
              <input
                type="text"
                ref={inputName}
                className="form-control"
                id="Name"
              />
            </div>
          </div>
          <div className="formColumn">
            <label htmlFor="Description" className="formLabel">
              Description:
            </label>
            <div className="col-sm-2">
              <input
                type="text"
                ref={inputDescription}
                className="form-control"
                id="Description"
              />
            </div>
          </div>
          <div className="formColumn">
            <label htmlFor="ImageName" className="formLabel">
              ImageName:
            </label>
            <div className="col-sm-2">
              <input
                type="text"
                ref={inputImageName}
                className="form-control"
                id="ImageName"
              />
            </div>
          </div>
          <div className="formColumn">
            <label htmlFor="Rating" className="formLabel">
              Rating:
            </label>
            <div className="col-sm-2">
              <input
                type="text"
                ref={inputRating}
                className="form-control"
                id="Rating"
              />
            </div>
          </div>
          <div className="mb-2">
            <button className="btn btn-primary" type="submit">
              Save
            </button>
          </div>
        </form>
      </div>
    ),
    <button
      key="2"
      className="btn btn-success"
      onClick={() => dispatch(toggleProductForm())}
    >
      {showProductForm ? "Cancel" : "Add"}
    </button>,
  ];
}

export default NewProduct;
