import React from "react";
import "../components/pagesCss/Home.css";
import "../components/pagesCss/GeneralPageCss.css";
import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";

function Home(comments) {
  const movieUrl = (category) =>
    `https://api.themoviedb.org/3/discover/movie?api_key=ad40cc6b3df48398b3ba8f28020bca29&language=en&sort_by=popularity.desc&include_adult=false&include_video=false&page=1&with_watch_monetization_types=flatrate&with_genres=${category}`;
  const fetchMovies = createAsyncThunk("movie/fetch", (category) => {
    return fetch(movieUrl(category))
      .then((response) => response.json())
      .then((json) => json.results)
      .then((search) =>
        search.map((m) => ({
          ...m,
          poster_path: imageUrl(m.poster_path),
          backdrop_path: imageUrl(m.backdrop_path),
          isFavorite: false,
        }))
      );
  });
  return <div className="container general-page">Home</div>;
}

export default Home;
