import React, { useContext, useEffect, useRef } from "react";
// import { Context } from "../Context";
import { useSelector } from "react-redux";
import { type } from "@testing-library/user-event/dist/type";
import {
  addItem,
  setProductForm,
  setProductToEdit,
  toggleProductForm,
  updateItem,
  showProductForm,
  productToEdit,
} from "../../features/form-slice";
import { useDispatch } from "react-redux";

function NewProduct() {
  // const state = useSelector((state) => state.form);
  const dispatch = useDispatch();

  const inputName = useRef(null);
  const inputDescription = useRef(null);

  useEffect(() => {
    if (productToEdit) {
      inputName.current.value = productToEdit.name;
      inputDescription.current.value = productToEdit.description;
    }
  }, [productToEdit]);

  const handleSave = (e) => {
    e.preventDefault();

    var item = {
      id: productToEdit ? productToEdit.id : 0,
      name: inputName.current.value,
      description: inputDescription.current.value,
    };

    fetch("https://localhost:7201/api/Products", {
      method: productToEdit ? "put" : "post",
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + localStorage.getItem("token"),
      },
      body: JSON.stringify(item),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error(response.status);
        }
        if (productToEdit) {
          return response;
        } else {
          return response.json();
        }
      })
      .then((data) => {
        if (productToEdit) {
          dispatch(updateItem(item));
        } else {
          dispatch(addItem(data));
        }
        dispatch(setProductForm(false));
      })
      .catch((error) => {
        console.error(error);
      });
  };

  return [
    showProductForm && (
      <div className="container" key="1">
        <h2>{productToEdit ? "Edit" : "New"} Product</h2>
        <form onSubmit={handleSave}>
          <div className="row mb-3">
            <label htmlFor="Name" className="col-sm-2 col-form-label">
              Name:
            </label>
            <div className="col-sm-2">
              <input
                type="text"
                ref={inputName}
                className="form-control"
                id="Name"
              />
            </div>
          </div>
          <div className="row mb-3">
            <label htmlFor="Description" className="col-sm-2 col-form-label">
              Description:
            </label>
            <div className="col-sm-2">
              <input
                type="text"
                ref={inputDescription}
                className="form-control"
                id="Description"
              />
            </div>
          </div>
          <div className="mb-2">
            <button className="btn btn-primary" type="submit">
              Save
            </button>
          </div>
        </form>
      </div>
    ),
    <button
      key="2"
      className="btn btn-success"
      onClick={() => dispatch(toggleProductForm())}
    >
      {showProductForm ? "Cancel" : "Add"}
    </button>,
  ];
}

export default NewProduct;
